(function(){var n;n=angular.module("answersControllers",[]),n.controller("AnswersController",function(n,e){return e.get("/questions/"+n.questionId+"/answers/").success(function(e){return n.answers=e}),n.currentPage=0,n.pageSize=5,n.numberOfPages=function(){return Math.ceil(n.answers.length/n.pageSize)},n.sendAnswer=function(){var s;return s={answer:{body:n.body}},e.post("/questions/"+n.questionId+"/answers/",s).success(function(e){return n.answers.push(e),n.body=""})}})}).call(this);