(function(){var n;n=angular.module("commentsControllers",[]),n.controller("CommentsController",function(n,e){return e.get("/news/"+n.newsId+"/comments/").success(function(e){return n.comments=e}),n.currentPage=0,n.pageSize=5,n.numberOfPages=function(){return Math.ceil(n.comments.length/n.pageSize)},n.sendComment=function(){var t;return t={comment:{body:n.body}},e.post("/news/"+n.newsId+"/comments/",t).success(function(e){return n.comments.push(e),n.body=""})}})}).call(this);